CREATE TABLE people(
    person_id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
    last_name VARCHAR(255),
    first_name VARCHAR(255),
    `address` VARCHAR (255),
    city VARCHAR(255)
);

CREATE OR REPLACE VIEW `platzi_people` AS
    SELECT * FROM platziblog.people;

ALTER TABLE people
ADD COLUMN `date_of_birth` DATETIME AFTER `city`;

ALTER TABLE people
CHANGE COLUMN `date_of_birth` `vdate_of_birth` VARCHAR(30);

ALTER TABLE people
DROP COLUMN `vdate_of_birth`;

