CREATE TABLE categorias(
    categoria_id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
    nombre_categoria VARCHAR(30) NOT NULL
);

CREATE TABLE etiquetas(
    etiqueta_id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
    nombre_etiqueta VARCHAR(30) NOT NULL
);

CREATE TABLE usuarios(
    usuario_id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
    `login` VARCHAR(30) NOT NULL,
    `password` VARCHAR(32) NOT NULL,
    nickname VARCHAR(40) NOT NULL UNIQUE,
    email VARCHAR(40) NOT NULL UNIQUE
);

CREATE TABLE posts(
    post_id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
    titulo VARCHAR(130) NOT NULL,
    fecha_publicacion TIMESTAMP,
    contenido TEXT NOT NULL,
    estatus CHAR(8) DEFAULT 'Activo',
    usuario_id INT NOT NULL,
    categoria_id INT NOT NULL
);

ALTER TABLE posts
ADD INDEX post_usuarios_idx (usuario_id ASC);
ALTER TABLE posts
ADD CONSTRAINT fk_posts_usuarios
FOREIGN KEY (usuario_id) 
REFERENCES usuarios(usuario_id)
ON DELETE NO ACTION
ON UPDATE CASCADE
;

ALTER TABLE posts
ADD INDEX post_categorias_idx (categoria_id ASC);
ALTER TABLE posts
ADD CONSTRAINT fk_post_categorias
FOREIGN KEY (categoria_id)
REFERENCES categorias(categoria_id)
ON DELETE NO ACTION
ON UPDATE NO ACTION;

CREATE TABLE comentarios(
    comentario_id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
    cuerpo_comentario TEXT NOT NULL,
    usuario_id INT NOT NULL,
    post_id INT NOT NULL
);

ALTER TABLE comentarios
ADD INDEX comentarios_usuarios_idx (usuario_id ASC);
ALTER TABLE comentarios
ADD CONSTRAINT fk_comentarios_usuarios
FOREIGN KEY (usuario_id) 
REFERENCES usuarios(usuario_id)
ON DELETE NO ACTION
ON UPDATE NO ACTION
;
ALTER TABLE comentarios
ADD INDEX comentarios_posts_idx (post_id ASC);
ALTER TABLE comentarios
ADD CONSTRAINT fk_comentarios_post
FOREIGN KEY (post_id) 
REFERENCES posts(post_id)
ON DELETE NO ACTION
ON UPDATE NO ACTION
;

/*tabla transitiva*/
CREATE TABLE posts_etiquetas(
    post_etiqueta_id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
    post_id INT NOT NULL,
    etiqueta_id INT NOT NULL
);
ALTER TABLE posts_etiquetas
ADD INDEX postsetiquetas_posts_idx (post_id ASC);
ALTER TABLE posts_etiquetas
ADD CONSTRAINT fk_postsetiquetas_posts
FOREIGN KEY (post_id) 
REFERENCES posts(post_id)
ON DELETE NO ACTION
ON UPDATE NO ACTION
;
ALTER TABLE posts_etiquetas
ADD INDEX postsetiquetas_etiquetas_idx (etiqueta_id ASC);
ALTER TABLE posts_etiquetas
ADD CONSTRAINT fk_postsetiquetas_etiquetas
FOREIGN KEY (etiqueta_id) 
REFERENCES etiquetas(etiqueta_id)
ON DELETE NO ACTION
ON UPDATE NO ACTION
;

